import { dominio } from "../dominio.js";
export default {
  data() {
    return {
      id: this.$route.params && this.$route.params.id,
      users: [],
      branchOffices: [],
      formAssign: {
        user_id: "",
        branch_office_id: "",
      },
    };
  },
  created() {
    this.showUser();
    this.showBranchOffices();
  },
  methods: {
    returnRo() {
      this.$router.push("/panel/show-ro");
    },
    showUser() {
      let url = dominio.url + "/api/mostrar-usuarios-comerciales";
      axios.get(url).then((res) => {
        this.users = res.data.users;
      });
    },
    showBranchOffices() {
      let url = dominio.url + "/api/mostrar-sucursales";
      axios.get(url).then((res) => {
        this.branchOffices = res.data.branchOffices;
      });
    },
    assignRo() {
      let url = dominio.url + "/api/assignar-ro-hijo/" + this.id;
      axios
        .post(url, this.formAssign)
        .then((res) => {
          if (res.data.code == 200) {
            toastr.success(res.data.message);
            this.$router.push("/panel/show-ro");
          }
          if (res.data.code == 500) {
            toastr.error(res.data.message);
          }
        })
        .catch((error) => {
          this.errors = error.response.data.errors;
        });
    },
  },
};
